package JavaPrograms;

public class EvenOrOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int number =7;
		if((number%2)==0){
			System.out.println("Even number : " + number);
		} 
		else{
		
			System.out.println("Odd number : " + number);
		}
		
	}

}
