package JavaPrograms;

public class Pyramid_One {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int i=1;i<=5;i++){
			for(int j=0;j<i;j++){
				System.out.print("*");
			}
			
			System.out.println(" ");
		}
		
	}

}


/*
Output of the above program would be
*
**
***
****
*****
*/